<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Email_model extends CI_Model{	function __construct(){		parent::__construct();		$this->load->database();	}	function get_emailtemplate_info(){		$this->db->select('*');		$this->db->from('email_template'); 		return $this->db->get()->result_array();	}	function get_emailtemplatewise_info($id){		$this->db->select('*');		$this->db->from('email_template');		$this->db->where('emailtemplate_id',$id);				return $this->db->get()->result_array();	}	function update_template_info($id,$data1){		$this->db->where('emailtemplate_id',$id);		$this->db->update('email_template', $data1);	}}?>