ent_sortorder == 'asc' )    {    var changeorder = Total_Data;    $('.drag-body tr').each(function() {    data_id = $(this).find('td:first').attr('class').substring(6);    data_id = $.trim(data_id);    selector = '#couponorder_'+data_id;    $(selector).val(changeorder--);    });    }    else    {    var changeorder = 1;    $('.drag-body tr').each(function() {    data_id = $(this).find('td:first').attr('class').substring(6);    data_id = $.trim(data_id);    selector = '#couponorder_'+data_id;    $(selector).val(changeorder++);    });    }    }    // Ajax Call - start //    jQuery.ajax({    url:Ajax_URL,    type: "POST",			    dataType: "json",    data: $(document.couponListForm).serializeArray(),    success:function(response) {    //alert (response);    }    });    // Ajax Call - end //    }    });    function confirmDelete()    {    var agree=confirm("Are you sure you want to delete this record?");    if (agree)    return TRUE;    else    return false;    }</script>