  </div>    </div></div><script>    $(document).on("click", ".script", function () {        var title = $(this).data('title');        $(".modal-title").text(title);        var textAreaContent = $(this).data('text');        $("#sourceText").val( textAreaContent );    });    /* Copy to clipboard functionality */    $(document).on("click" , '.copy' , function () {        $("#sourceText").select();        document.execCommand('copy');    });    $('#dynamic-table').DataTable( {        "aoColumnDefs": [{ "bSortable": false, "aTargets": [9] }]    });</script>